INSERT INTO `phpyun_company_job` VALUES ('9', '20', '影视策划主创', '40', '40', '76', '544', '20', '236', '0', '', '128', '55', '41', '15', '57', '62', '69', '72', '<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	岗位职责：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<span>1</span>、深入了解行业情况，参与公司项目的创意构思、文案及客户提案；\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<span>2</span>、前期提案、设计创意说明及后期结案报告等服务；\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<span>3</span>、独立撰写各类稿件、策划方案、报告等。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	岗位要求：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<span>1</span>、本科以上学历，广告学、新闻传播学、中文等专业，；\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<span>2</span>、两年以上市场策划及文案工作经验，熟悉广告设计行业，有影视广告脚本成功案例者优先；\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<span>3</span>、对影视广告策划热爱，擅长图文创意；\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<span>4</span>、熟悉专业创意方法，思维敏捷，洞察力强，文字功底扎实，语言表达能力强；\r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-size:14px;font-family:sans-serif;\">\r\n	<span>5</span>、对项目具有精准及独到的理解能力，具有较强的逻辑思维和创新精神，有独到的市场观点。\r\n</p>', '0', '0', '1428027156', '1446134400', '57', '1432277160', '0', '0', '1', '0', '88', '', '', '', '23', '30', '20', '3', '0', '0', '0', '0', '0', './upload/company/20150403/make_S_14318503237.PNG', '0', '0', '1', '2', '1', '0', '0');
