INSERT INTO `phpyun_company_job` VALUES ('13', '18', '宠物食品区域销售经理', '39', '45', '103', '819', '20', '235', '0', '', '46', '55', '41', '16', '61', '62', '69', '72', '<p>\r\n	工作地点：全国，就近分配\r\n</p>\r\n<p>\r\n	岗位职责：\r\n</p>\r\n<p>\r\n	1、负责管理辖区的销售任务达成，带动销售团队一起完成销售目标； <br />\r\n2、制定客户发展战略计划，并细化到每个客户销售目标与促销，并监督执行； <br />\r\n3、 客户的销售统计分析、支援客户的计划方案、经营分析指导；<br />\r\n4、根据销售计划开拓与完善经销网络， 销售渠道与客户的管理，经销商评估及调整；&nbsp; <br />\r\n5、 销售活动与售后服务的指导及抱怨处理； <br />\r\n6、负责管辖内客户的开发、业务、培训、进货、回款等协销工作； <br />\r\n7、参与经销商销售谈判和签定合同；&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n8、分析汇总市场信息，提报宠物食品改善或宠物食品开发建议，洞察、预测渠道危机及时提出改善意见；&nbsp;&nbsp; <br />\r\n9、根据销售预算进行过程控制，降低销售费用；&nbsp;&nbsp; <br />\r\n10、 销售人员的招募、培训、调配、业绩评估，关注所辖人员的思想动态，及时沟通解决； <br />\r\n11、完成部门交办的临时性工作 。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	岗位要求：\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	◆教育背景:市场营销专业或相关专业本科以上学历。<br />\r\n◆培训经历:受过市场营销、管理技能开发、财务知识、宠物食品知识等方面的培训。<br />\r\n◆工作经验:4年以上企业销售管理工作经验。<br />\r\n◆技能技巧:<br />\r\n1、对市场营销工作有较深刻认知，对宠物市场有良好的判断能力；<br />\r\n2、有较强的组织管理能力；<br />\r\n3、有较强的市场销售经验和市场嗅觉；<br />\r\n4、熟练操作办公软件。<br />\r\n◆工作态度:<br />\r\n1、自觉严谨、公正律己、敬业、豁达自信；<br />\r\n2、高度的工作热情，良好的团队合作精神，较强的观察力和应变能力。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>', '0', '0', '1428040710', '1438358400', '141', '1428040710', '0', '0', '1', '0', '86', '', '', '广东海大集团集团股份有限公司', '79', '32', '20', '0', '0', '0', '1', '0', '0', './upload/company/20150402/make_S_14377166328.JPG', '0', '0', '0', '1', '1', '0', '0');
