INSERT INTO `phpyun_company_job` VALUES ('35', '31', '土地拓展总监', '43', '41', '80', '617', '1', '36', '0', '', '53', '54', '41', '127', '57', '62', '65', '72', '<p class=\"MsoNormal\">\r\n	职位描述：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;background:#FFFFFF;\">\r\n	岗位职责：<br />\r\n1<span>、协助获取及跟进土地项目信息，准备开发建设，进行项目考察、可行性研究及经济测算；</span><br />\r\n2<span>、协助执行项目收购、合作及土地招拍挂工作，参与对外谈判；</span><br />\r\n3<span>、分析现有项目的市场情况，以及国家、地方与行业相关的最新法规、规范、规程及标准对公司的影响。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;background:#FFFFFF;\">\r\n	任职资格：<br />\r\n1<span>、本科及以上学历，房地产相关专业或金融、经济专业优先；</span><br />\r\n2<span>、五年以上相关工作经验，熟悉房地产前期土地评估、拓展、谈判及开发规程，了解房地产市场发展趋势及规律，具有土地项目运作成功经验；</span><br />\r\n3<span>、具有房地产行业知名企业同类职位工作经验者优先；</span><br />\r\n4<span>、具备较强的学习能力及团队精神，能适应压力；</span><br />\r\n5<span>、能够适应出差</span>。\r\n</p>', '0', '0', '1428476978', '1439395200', '10', '1428476978', '0', '0', '1', '0', '88', '', '', '北京优才库管理咨询有限公司', '22', '27', '1', '3', '0', '0', '0', '0', '0', './upload/company/20150408/make_S_14294879844.JPG', '0', '0', '1', '1', '1', '0', '0');
