INSERT INTO `phpyun_company_job` VALUES ('42', '20', '销售经理（南宁）', '40', '36', '53', '213', '21', '255', '0', '', '128', '55', '41', '13', '57', '62', '68', '72', '<p style=\"font-size:14px;font-family:sans-serif;\">\r\n	岗位职责：&nbsp;<br />\r\n1、协助销售经理，在西南地区销售及推广公司数字展示产品和服务；&nbsp;<br />\r\n2、根据市场营销计划，完成部门销售目标；&nbsp;<br />\r\n3、拜访老客户，维持好老客户关系；&nbsp;<br />\r\n4、发展新客户，增加产品销售业绩。&nbsp;<br />\r\n任职资格：&nbsp;<br />\r\n1、专科及以上学历，市场营销等相关专业；&nbsp;<br />\r\n2、1年以上销售岗位工作经验，业绩突出者优先；&nbsp;<br />\r\n3、性格外向、反应敏捷、表达能力强，具有较强的沟通能力及交际技巧，具有亲和力；&nbsp;<br />\r\n4、具备一定的市场分析及判断能力，良好的客户服务意识；&nbsp;<br />\r\n5、有责任心，能承受较大的工作压力。&nbsp;\r\n</p>\r\n<p style=\"font-size:14px;font-family:sans-serif;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;font-family:sans-serif;\">\r\n	求职者请关注凡拓招聘微信公众号获取最新信息：凡拓招聘\r\n</p>', '0', '0', '1428553182', '1446652800', '25', '1428553182', '0', '0', '1', '0', '88', '', '', '广州凡拓数字创意科技股份有限公司深圳分公司', '23', '30', '20', '3', '0', '0', '0', '0', '0', './upload/company/20150403/make_S_14318503237.PNG', '0', '0', '1', '1', '1', '0', '0');
